package CFS_Loop_Statements;

public class Do_While_Loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*process      statement
		 *              logic
		 *              condition	 */
		int a=0;
		do
		{
			System.out.println(a);
			a=a+1;
		}
		while(a<10);
	}

}


	